import{_ as e,c as l,o as d,a2 as a}from"./chunks/framework.D5VGNaZP.js";const o=JSON.parse('{"title":"吉旦饼输入法","description":"","frontmatter":{},"headers":[],"relativePath":"docs/wafel.md","filePath":"docs/wafel.md"}'),s={name:"docs/wafel.md"};function n(f,t,i,r,g,x){return d(),l("div",null,[...t[0]||(t[0]=[a('<h1 id="吉旦饼输入法" tabindex="-1">吉旦饼输入法 <a class="header-anchor" href="#吉旦饼输入法" aria-label="Permalink to &quot;吉旦饼输入法&quot;">​</a></h1><p><a href="https://lost-melody.github.io/wafel" target="_blank" rel="noreferrer"><strong>吉旦饼</strong></a>，是阿吉、王牌饼干发起的一项输入法方案。它采用乱序字根设计、三码定长、延迟顶字。「吉旦饼」这个名字，来自三名主要开发人：阿吉，forFudan，王牌饼干。取义于 华夫饼 (wafel)，其汉语翻译应有「鸡蛋饼」之义。</p><p>输入三码后，对应的三码单字不会上屏，而是将此汉字暂留在预选区，等到第七码的时候，才顶出第一字。每输入四码时，进入临时四码模式，可以选择 Tab 上屏对应的四码单字。因此可看作加强版的「五三顶」输入法。</p><p>鸡蛋饼不取第三字根，故而拆分起来更加简单，思维压力较小。三码一字，也使得输入的节奏非常舒服。</p><table><thead><tr><th style="text-align:left;">特点</th><th style="text-align:left;">宇浩·星陈</th><th style="text-align:left;">吉旦饼</th></tr></thead><tbody><tr><td style="text-align:left;">使用最新版本宇浩拆分</td><td style="text-align:left;">✅</td><td style="text-align:left;">✅</td></tr><tr><td style="text-align:left;">支持到 CJK ext-J</td><td style="text-align:left;">✅</td><td style="text-align:left;">（四码模式下）</td></tr><tr><td style="text-align:left;">兼容台湾字形拆分</td><td style="text-align:left;">✅</td><td style="text-align:left;">❌</td></tr><tr><td style="text-align:left;">字根双编码</td><td style="text-align:left;">✅</td><td style="text-align:left;">✅</td></tr><tr><td style="text-align:left;">三码方案</td><td style="text-align:left;">❌</td><td style="text-align:left;">✅</td></tr><tr><td style="text-align:left;">四码方案</td><td style="text-align:left;">✅</td><td style="text-align:left;">（支持四码加 Tab 键输入）</td></tr><tr><td style="text-align:left;">单字派码长</td><td style="text-align:left;">~3.15</td><td style="text-align:left;">~2.65</td></tr><tr><td style="text-align:left;">一简二简个数</td><td style="text-align:left;">~600</td><td style="text-align:left;">~50</td></tr><tr><td style="text-align:left;">繁简通打低极选重率 (0.2%)</td><td style="text-align:left;">✅</td><td style="text-align:left;">（四码模式下）</td></tr><tr><td style="text-align:left;">大码使用 Z 键</td><td style="text-align:left;">❌</td><td style="text-align:left;">✅</td></tr><tr><td style="text-align:left;">小码使用 Z 键</td><td style="text-align:left;">✅</td><td style="text-align:left;">✅</td></tr><tr><td style="text-align:left;">相似字形字根同大码聚类</td><td style="text-align:left;">✅</td><td style="text-align:left;">❌</td></tr><tr><td style="text-align:left;">小码使用拼音中的字母</td><td style="text-align:left;">✅</td><td style="text-align:left;">(频率前50字根乱序，其他音托)</td></tr><tr><td style="text-align:left;">小码 85% 使用拼音首字母</td><td style="text-align:left;">✅</td><td style="text-align:left;">❌</td></tr><tr><td style="text-align:left;">双手互击率 60%</td><td style="text-align:left;">✅</td><td style="text-align:left;">✅</td></tr><tr><td style="text-align:left;">严格优化键位分布</td><td style="text-align:left;">✅</td><td style="text-align:left;">✅</td></tr><tr><td style="text-align:left;">Z 键反查</td><td style="text-align:left;">✅</td><td style="text-align:left;">❌</td></tr></tbody></table>',5)])])}const c=e(s,[["render",n]]);export{o as __pageData,c as default};
