import{_ as i}from"./chunks/fancha.BDy_nOl5.js";import{_ as e}from"./chunks/fuhaoshuru.BBvUQeH9.js";import{_ as r,c as o,o as t,a2 as l,m as d}from"./chunks/framework.D5VGNaZP.js";const q=JSON.parse('{"title":"各平台下载和安装","description":"","frontmatter":{},"headers":[],"relativePath":"install.md","filePath":"install.md"}'),c={name:"install.md"};function s(h,a,n,p,u,m){return t(),o("div",null,[...a[0]||(a[0]=[l('<h1 id="各平台下载和安装" tabindex="-1">各平台下载和安装 <a class="header-anchor" href="#各平台下载和安装" aria-label="Permalink to &quot;各平台下载和安装&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">下载地址</p><p><a href="http://zhuyuhao.ysepan.com/" target="_blank" rel="noreferrer">永硕网盘</a><br><a href="https://github.com/forFudan/yuhao/releases/" target="_blank" rel="noreferrer">Github</a><br><a href="https://gitee.com/forFudan/yuhao/releases/" target="_blank" rel="noreferrer">Gitee 码云</a></p></div><h2 id="码表直接挂载" tabindex="-1">码表直接挂载 <a class="header-anchor" href="#码表直接挂载" aria-label="Permalink to &quot;码表直接挂载&quot;">​</a></h2><p>本方案的码表可以在任何手机或电脑的输入法 App 上挂载并使用，比如百度、搜狗、落格、小小等。由于本方案只使用 25 键，故而可以完美挂载在任何五笔字型输入法的平台上，并使用<code>Z</code>键的功能。</p><p>码表在 /mabiao 文件夹下。</p><p>码表因其格式，可以通用于多种平台。以下为几种主要格式的介绍：</p><ul><li>编码 空格 字符，UTF-8。此为<strong>百度格式</strong>，适用于百度输入法、搜狗输入法、清歌输入法。加上表头后，也适用于小小输入法、微软输入法、Fcitx 输入法。</li><li>字符 Tab 编码，UTF-8。此为<strong>多多格式</strong>，适用于落格输入法、小胖输入法。加上表头后，也适用于 RIME 输入法。还能用作 <a href="https://typer.owenyang.top" target="_blank" rel="noreferrer">木易跟打器</a> 的词提上传。</li><li>编码 Tab 字符，UTF-8。此为<strong>大竹格式</strong>，用于大竹词提生成。</li></ul><p>码表一共有以下三种：</p><ol><li>宇浩星陈·陆标简码.txt，为简化汉字设置简码，码长约为 3.25。</li><li>宇浩星陈·繁简通打.txt，为繁简通打设置简码，码长约为 3.30。</li><li>宇浩星陈·单字全码.txt，单字全码码表。</li></ol><h3 id="百度-搜狗手机输入法" tabindex="-1">百度/搜狗手机输入法 <a class="header-anchor" href="#百度-搜狗手机输入法" aria-label="Permalink to &quot;百度/搜狗手机输入法&quot;">​</a></h3><p>百度手机输入法挂载码表方法如下：</p><ul><li>将 /baidu 下的码表下载到设备上</li><li>进入百度输入法 iOS 版</li><li>点击「我的」</li><li>点击右上方设置按钮</li><li>点击「输入设置」</li><li>点击「五笔输入」</li><li>点击「自定义方案」</li><li>点击「导入方案」</li><li>选择下载的码表文件，导入后即可使用</li></ul><p>「搜狗输入法」挂载码表方法同百度输入法。</p><p>「清歌输入法」码表同百度输入法。</p><h3 id="落格输入法" tabindex="-1">落格输入法 <a class="header-anchor" href="#落格输入法" aria-label="Permalink to &quot;落格输入法&quot;">​</a></h3><p>安装方法如下：</p><ul><li>将 /duoduo 下的码表下载到电脑上。</li><li>进入输入法程序。</li><li>选择「Wifi 传表」。</li><li>将码表传入手机中。</li><li>回到落格输入法的设置界面，在主码表中选择「yuhao」即可使用。</li></ul><h3 id="小小输入法" tabindex="-1">小小输入法 <a class="header-anchor" href="#小小输入法" aria-label="Permalink to &quot;小小输入法&quot;">​</a></h3><p>小小输入法码表是将 /baidu 下的「小小配置。txt」中的内容复制到码表文件顶部。</p><h2 id="rime-平台安装-小狼毫、鼠须管、同文、irime、仓输入法" tabindex="-1">RIME 平台安装（小狼毫、鼠须管、同文、iRime、仓输入法） <a class="header-anchor" href="#rime-平台安装-小狼毫、鼠须管、同文、irime、仓输入法" aria-label="Permalink to &quot;RIME 平台安装（小狼毫、鼠须管、同文、iRime、仓输入法）&quot;">​</a></h2><p>下面介绍的是挂载于 <a href="https://rime.im/" target="_blank" rel="noreferrer">RIME 平台（小狼毫、鼠须管、同文、iRime）</a> 的方案。我对它进行了深度定制，具有以下特点：</p><ul><li>提供至 CJK-J 区、兼容区、部首区超过 101972 个汉字的完整拆分、编码提示、字集提示。</li><li>支持自定义字符集过滤生僻字。常用字约一万字，包括 GB2312 汉字、国语常用字、其它常用汉字等。支持用户自定义修改。</li><li>提供四码只出单字功能，适合单字派。</li><li>提供生僻字后置、生僻字屏蔽功能。</li></ul><p>在安装了 Rime（小狼毫、鼠须管、同文、iRime）后，将安装包中的 /schema 文件夹下的<strong>所有文件</strong>复制到<strong>用户文件夹</strong>下（可以右击 Rime 图标后点击「用户文件夹」来打开）。点击「部署」之后即可使用。</p><p>宇浩输入法 windows 安装视频教程。</p>',24),d("iframe",{src:"//player.bilibili.com/player.html?aid=326571384&bvid=BV1xw411E7ak&cid=1414823103&p=1",scrolling:"no",border:"0",frameborder:"no",framespacing:"0",allowfullscreen:"true",style:{width:"100%","aspect-ratio":"1.778"}},null,-1),l('<p>/schema 下文件介绍：</p><ol><li>yustar.schema.yaml 为繁简通打设置简码，码长约为 3.30。</li><li>yustar_sc.schema.yaml 为简化汉字设置简码，码长约为 3.25。</li><li>yustar_tw.schema.yaml 为传统汉字台湾字形设置简码。<br><code>〔注：以上三个文件，全码排序完全一致，只是简码设置不同，主要为了增加输入效率。〕</code></li><li>yustar.dict.yaml 主码表，用来调用其他码表。</li><li>yustar.full.dict.yaml 单字全码码表。</li><li>yustar.quick.dict.yaml 简码码表。</li><li>yustar.adjusted.dict.yaml 字形调整码表。</li><li>yustar.words 宇浩核心词库。约 80000 个词语，频率较高。</li><li>yustar.words_literature 宇浩成语诗词词库。约 15000 个成语和诗词，除非有特殊需求，否则不建议开启。</li><li>yustar.symbols.dict.yaml 特殊符号码表。</li><li>yuhao_pinyin.schema.yaml 拼音配置文件，用于反查。</li><li>yuhao_pinyin.dict.yaml 拼音字典文件。</li><li>lua/yuhao/... 各种脚本。</li></ol><h3 id="单字拆分四重注解" tabindex="-1">单字拆分四重注解 <a class="header-anchor" href="#单字拆分四重注解" aria-label="Permalink to &quot;单字拆分四重注解&quot;">​</a></h3><p>提供至 CJK-J 区、兼容区、部首区超过 101972 个汉字的拆分、编码提示、字集提示。拆分提示中包括三重注解：</p><ol><li>该汉字的拆分。</li><li>该汉字的全码。使用大小写字母区分字根的姓名。</li><li>该汉字所在的字符集（CJK，CJK-A 到 CJK-J 区，兼容字等）。</li><li>该汉字的 Unicode 编码。</li></ol><p>用户还可通过 <code>Shift+Ctrl+C</code> 或 <code>斜杠 /</code> 切换拆分状态。</p><h3 id="自定义常用字符集" tabindex="-1">自定义常用字符集 <a class="header-anchor" href="#自定义常用字符集" aria-label="Permalink to &quot;自定义常用字符集&quot;">​</a></h3><p>本方案使用了自定的常用字符，将常用字一网打尽，避免了 RIME 内置字符集「GB2312 字太少，GBK 字太多」的问题。包括了以下一万个左右的字符：</p><ul><li>《通用规范汉字表》中定义的，在 GB2312 字集内的汉字</li><li>台湾的「国字常用字」</li><li>286 个大陆繁体字形</li><li>注音符号</li><li>「〇」符号</li></ul><h3 id="一键切换字符集" tabindex="-1">一键切换字符集 <a class="header-anchor" href="#一键切换字符集" aria-label="Permalink to &quot;一键切换字符集&quot;">​</a></h3><p>在输入过程中，用户可通过 <code>Shift+Ctrl+O</code> 循环选择三种字集过滤的方式。</p><ul><li>全字集显示。</li><li>只显示常用字符集（过滤）。</li><li>常用字符集中的汉字和词语优先显示（优先）。</li></ul><p>用户还可通过 <code>Shift+Ctrl+F</code> 进行简入繁出输入。</p><h3 id="z-键功能键" tabindex="-1">Z 键功能键 <a class="header-anchor" href="#z-键功能键" aria-label="Permalink to &quot;Z 键功能键&quot;">​</a></h3><h4 id="使用-z-键引导拼音反查" tabindex="-1">使用 Z 键引导拼音反查 <a class="header-anchor" href="#使用-z-键引导拼音反查" aria-label="Permalink to &quot;使用 Z 键引导拼音反查&quot;">​</a></h4><p>按下 Z 键，可以随时使用拼音输入词语，并实现反查。</p><p>反查字典中，繁简体同权重，繁体字形包括大陆古籍、台湾繁体、香港繁体、OpenCC 繁体四套字形标准。</p><p><img src="'+i+'" alt="反查"></p><h4 id="使用-z-键上屏历史输入" tabindex="-1">使用 Z 键上屏历史输入 <a class="header-anchor" href="#使用-z-键上屏历史输入" aria-label="Permalink to &quot;使用 Z 键上屏历史输入&quot;">​</a></h4><p>可以使用<code>z</code>键快速上屏最近的历史输入。</p><h3 id="输入特殊符号" tabindex="-1">输入特殊符号 <a class="header-anchor" href="#输入特殊符号" aria-label="Permalink to &quot;输入特殊符号&quot;">​</a></h3><p>本方案可以用编码输入特殊符号，包括标点符号、注音符号、日语假名（训令罗马字）等。只要输入引导符号：</p><ul><li><code>fh</code>引导注音符号。例如：ㄤ，<code>fhan</code></li><li><code>jm</code>引导日语假名。例如：あ，<code>jma</code></li><li><code>py</code>引导拼音字母。例如：ǎ，<code>pya</code></li><li><code>bd</code>引导中文标点。例如：分号，<code>bdfh</code></li><li><code>dy</code>引导德语字母。例如：ß，<code>dyss</code></li><li><code>yj</code>引导易经六十四卦符号。例如：䷾（既济），<code>yjkl</code>。</li><li><code>kk</code>引导其他符号。</li><li><code>/</code>引导所有符号。</li></ul><p><img src="'+e+'" alt="符号输入"></p><h3 id="精确造词" tabindex="-1">精确造词 <a class="header-anchor" href="#精确造词" aria-label="Permalink to &quot;精确造词&quot;">​</a></h3><p>输入过程中，按下反引号键（数字 1 的左侧）作为分隔，使用<strong>数字键或空格键</strong>依次选择单字，即可实现精确造词。</p><h3 id="全码屏蔽词语" tabindex="-1">全码屏蔽词语 <a class="header-anchor" href="#全码屏蔽词语" aria-label="Permalink to &quot;全码屏蔽词语&quot;">​</a></h3><p>一键屏蔽四码词语，同时保留简码词。热键为 <code>Shift+Ctrl+D</code>。适合保留简码词的单字简词派。</p><h3 id="全码后置" tabindex="-1">全码后置 <a class="header-anchor" href="#全码后置" aria-label="Permalink to &quot;全码后置&quot;">​</a></h3><p>将已经设置了简码的汉字的全码后置。热键为 <code>Shift+Ctrl+H</code>。</p>',30)])])}const k=r(c,[["render",s]]);export{q as __pageData,k as default};
