import{Z as e}from"./chunks/ZigenMap.COgZ9Y9d.js";import{c as l,o as d,a2 as a,H as i}from"./chunks/framework.D3o4Ln5n.js";import"./chunks/share.Bj6ex72H.js";const x=JSON.parse('{"title":"爛然星陳","description":"","frontmatter":{},"headers":[],"relativePath":"zht/docs/star.md","filePath":"zht/docs/star.md"}'),r={name:"zht/docs/star.md"},o=Object.assign(r,{setup(s){return(n,t)=>(d(),l("div",null,[t[0]||(t[0]=a('<h1 id="爛然星陳" tabindex="-1">爛然星陳 <a class="header-anchor" href="#爛然星陳" aria-label="Permalink to &quot;爛然星陳&quot;">​</a></h1><h2 id="簡介" tabindex="-1">簡介 <a class="header-anchor" href="#簡介" aria-label="Permalink to &quot;簡介&quot;">​</a></h2><p><strong>宇浩·星陳</strong>為宇浩系列推薦入門方案，採用了字根聚類同鍵排布模式（類似鄭碼、徐碼）。字形相似的字根會被聚攏在一道，排布在 25 個鍵位上。方案名取自《尚書大傳》之「明明上天，爛然星陳」。</p><p>由於大量字形相近的字根被放在了一個按鍵上，它對於初學者極爲友好，可以通過聯想記憶法，以「組」爲單位來記憶。比如：</p><ul><li><code>目且貝見</code>和<code>日曰早</code>都在 J 上。</li><li><code>亦文（夂）亥（豕）亡方</code>都在 T 上。</li><li><code>广麻鹿疒</code>都在 M 上。</li></ul><div class="zigen-font"><table><thead><tr><th style="text-align:left;">大碼</th><th style="text-align:left;">聚類字根</th><th>零散字根</th></tr></thead><tbody><tr><td style="text-align:left;">A</td><td style="text-align:left;">木來　㔾卩　亠高</td><td></td></tr><tr><td style="text-align:left;">B</td><td style="text-align:left;">皮又龴スコ癶马彐尸弓丑 　(橫折)</td><td>言 入</td></tr><tr><td style="text-align:left;">C</td><td style="text-align:left;">冂贝见冊雨　(下框) 　彳彡　(幾撇)</td><td>女</td></tr><tr><td style="text-align:left;">D</td><td style="text-align:left;">由电申車叀丰　白𠂤臼戶鬼向烏鳥斤爿片　鬥門</td><td>至 讠</td></tr><tr><td style="text-align:left;">E</td><td style="text-align:left;">亻𠂇隹　几殳風　乚彑母</td><td></td></tr><tr><td style="text-align:left;">F</td><td style="text-align:left;">未末　手扌龵　尤尢</td><td>一</td></tr><tr><td style="text-align:left;">G</td><td style="text-align:left;">丿竹气𧘇　乃廴</td><td>十 黑 艮</td></tr><tr><td style="text-align:left;">H</td><td style="text-align:left;">土士壴走龶　匚臣牙</td><td>犬 丂 户</td></tr><tr><td style="text-align:left;">I</td><td style="text-align:left;">宀穴　小⺌尚　二亍冫　火灬</td><td></td></tr><tr><td style="text-align:left;">J</td><td style="text-align:left;">日曰𫩏早古　上丄下丅丁示　目且見貝　缶矢</td><td>王</td></tr><tr><td style="text-align:left;">K</td><td style="text-align:left;">丶乀八食　寸了子予　立辛　弋戈戊　　卯</td><td>纟</td></tr><tr><td style="text-align:left;">L</td><td style="text-align:left;">兀丌耳　口舌　三川巛巜</td><td></td></tr><tr><td style="text-align:left;">M</td><td style="text-align:left;">广疒麻鹿　千壬禾夭毛豸　乌鸟　刀刂　辰</td><td></td></tr><tr><td style="text-align:left;">N</td><td style="text-align:left;">水氺　幺厶匕长　干甫</td><td></td></tr><tr><td style="text-align:left;">O</td><td style="text-align:left;">己已巳巴　大夫𡗗　也乜　辶之</td><td>糸 钅</td></tr><tr><td style="text-align:left;">P</td><td style="text-align:left;">止龰足齒　酉西襾　生牛</td><td>工</td></tr><tr><td style="text-align:left;">Q</td><td style="text-align:left;">丷䒑丬羊⺶⺷米　自身</td><td>虫</td></tr><tr><td style="text-align:left;">R</td><td style="text-align:left;">心忄　人合僉　咼骨冎　九瓦</td><td></td></tr><tr><td style="text-align:left;">S</td><td style="text-align:left;">艹廾卅卌龷世革　而面</td><td>用 力 乂</td></tr><tr><td style="text-align:left;">T</td><td style="text-align:left;">方文夂攵亦亡亥豕</td><td>金 门</td></tr><tr><td style="text-align:left;">U</td><td style="text-align:left;">爪瓜　罒皿　長镸髟　丨卜虎虍鹵　〢非其业　山巾</td><td>舟</td></tr><tr><td style="text-align:left;">V</td><td style="text-align:left;">田甲里禺魚　(田不出頭或下出頭) 　石厂丆不</td><td>习</td></tr><tr><td style="text-align:left;">W</td><td style="text-align:left;">月勹　凵丩屮丱　(上框)</td><td>囗 馬 儿</td></tr><tr><td style="text-align:left;">X</td><td style="text-align:left;">冖𠂊夕饣欠鱼　车七</td><td>衤</td></tr><tr><td style="text-align:left;">Y</td><td style="text-align:left;">乛亅⺄乙飛</td><td>阝</td></tr></tbody></table></div><h2 id="字根小碼" tabindex="-1">字根小碼 <a class="header-anchor" href="#字根小碼" aria-label="Permalink to &quot;字根小碼&quot;">​</a></h2><p>星陳方案使用強音託，字根小碼 85% 以上情況下都是漢語拼音的第一個字母（yi 取 y，yu 取 v）。故而大部分的字根小碼不用特別記憶，可以猜出來。</p><p>由於很多字根拼音只有兩個字母，首字母容易被佔用，故而 15% 情況下會選取其後續字母（主元音）作爲音託。只要特別記住以下字根即可：</p><table><thead><tr><th style="text-align:left;">小碼</th><th style="text-align:left;">字根</th><th style="text-align:left;">解釋</th></tr></thead><tbody><tr><td style="text-align:left;">a</td><td style="text-align:left;">三八麻</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">o</td><td style="text-align:left;">又尤由舟 艹</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">e</td><td style="text-align:left;">人文了业冊</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">i ㄧ</td><td style="text-align:left;">七 心 臼</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">i</td><td style="text-align:left;">矢世十 止自</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">u</td><td style="text-align:left;">不夂鬥</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">r ㄦ</td><td style="text-align:left;">二儿而</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;">v</td><td style="text-align:left;">上小口</td><td style="text-align:left;">讓出聲母給低頻根</td></tr></tbody></table><h2 id="字根圖" tabindex="-1">字根圖 <a class="header-anchor" href="#字根圖" aria-label="Permalink to &quot;字根圖&quot;">​</a></h2>',11)),i(e,{"default-scheme":"star","column-min-width":"1.5rem"})]))}});export{x as __pageData,o as default};
